import React, { useState } from 'react';

const MatchCard = ({ match, onAccept, onReject }) => {
  const [status, setStatus] = useState(match.status || 'pending'); // pending, accepted, rejected

  const handleAccept = () => {
    setStatus('accepted');
    if (onAccept) onAccept(match.id);
  };

  const handleReject = () => {
    setStatus('rejected');
    if (onReject) onReject(match.id);
  };

  return (
    <div className="match-card listing-card">
      <div className="listing-card-image-wrapper">
        <img
          className="listing-card-image"
          src={match.profileImage && match.profileImage.startsWith('http') ? match.profileImage : 'https://images.unsplash.com/photo-1560448204-e02f11c3d0e2?ixlib=rb-1.2.1&auto=format&fit=crop&w=500&q=80'}
          alt={match.name}
          onError={e => { e.target.onerror = null; e.target.src = 'https://images.unsplash.com/photo-1560448204-e02f11c3d0e2?ixlib=rb-1.2.1&auto=format&fit=crop&w=500&q=80'; }}
        />
        <div className="listing-card-compatibility listing-card-compatibility-inner from-green-400 to-emerald-500">
          <span className="listing-card-compatibility-value">{match.compatibility}%</span>
        </div>
        {match.verified && (
          <div className="listing-card-owner-verified">✓</div>
        )}
      </div>
      <div className="listing-card-content">
        <div className="listing-card-title-row">
          <h3 className="listing-card-title">{match.name}, {match.age}</h3>
        </div>
        <div className="listing-card-info-row">
          <span className="listing-card-location">{match.course} • {match.year}</span>
          <span className="listing-card-distance">{match.college}</span>
        </div>
        <div className="listing-card-description">{match.bio}</div>
        <div className="listing-card-amenities">
          {(match.interests || []).slice(0, 4).map((interest, idx) => (
            <span key={idx} className="listing-card-amenity">{interest}</span>
          ))}
        </div>
        <div className="listing-card-actions">
          {status === 'pending' && (
            <>
              <button className="btn-primary" onClick={handleAccept}>Accept</button>
              <button className="btn-secondary" onClick={handleReject}>Reject</button>
            </>
          )}
          {status === 'accepted' && (
            <button className="btn-primary" disabled>Accepted</button>
          )}
          {status === 'rejected' && (
            <button className="btn-secondary" disabled>Rejected</button>
          )}
        </div>
      </div>
    </div>
  );
};

export default MatchCard;
