import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import Sidebar from '../components/Sidebar';
import MobileMenu from '../components/MobileMenu';
import authService from '../services/authService';
import userService from '../services/userService';
import './Profile.modern.css';

const defaultProfile = {
  name: '',
  email: '',
  phone: '',
  college: '',
  course: '',
  year: '',
  avatar: '',
  location: '',
  budget: {
    min: 5000,
    max: 8000
  },
  lifestyle: {
    smoking: 'Non-smoker',
    sleepSchedule: 'Flexible',
    cleanliness: 'Moderately clean',
    studyHabits: 'Balanced',
    social: 'Moderately social'
  },
  preferences: {
    roommates: 1,
    gender: 'Any',
    amenities: [],
    petFriendly: false
  },
  trustScore: 0,
  verified: false
};

const Profile = () => {
  const navigate = useNavigate();
  const [activeTab, setActiveTab] = useState('personal');
  const [isEditing, setIsEditing] = useState(false);
  const [isMobileMenuOpen, setIsMobileMenuOpen] = useState(false);
  const [isUploading, setIsUploading] = useState(false);
  const [isLoading, setIsLoading] = useState(true);
  const [error, setError] = useState(null);
  const [userProfile, setUserProfile] = useState(defaultProfile);

  useEffect(() => {
    const fetchUserProfile = async () => {
      try {
        setIsLoading(true);
        setError(null);

        // Get fresh user data from backend
        const userResponse = await authService.getCurrentUser();
        if (userResponse.success && userResponse.data) {
          setUserProfile({
            name: userResponse.data.name || userResponse.data.fullname || '',
            email: userResponse.data.email || '',
            phone: userResponse.data.phone || '',
            college: userResponse.data.college || '',
            course: userResponse.data.course || '',
            year: userResponse.data.year || '',
            avatar: userResponse.data.profilePicture || userResponse.data.avatar || '',
            location: userResponse.data.location || '',
            budget: userResponse.data.budget || { min: 5000, max: 8000 },
            lifestyle: userResponse.data.preferences?.lifestyle || {
              smoking: 'Non-smoker',
              sleepSchedule: 'Flexible',
              cleanliness: 'Moderately clean',
              studyHabits: 'Balanced',
              social: 'Moderately social'
            },
            preferences: {
              roommates: userResponse.data.preferences?.roommates || 1,
              gender: userResponse.data.preferences?.gender || 'Any',
              amenities: userResponse.data.preferences?.amenities || [],
              petFriendly: userResponse.data.preferences?.petFriendly || false,
            },
            trustScore: userResponse.data.trustScore || 85,
            verified: userResponse.data.verified || false
          });
        }
      } catch (err) {
        setError(err.message || 'Failed to fetch profile data');
      } finally {
        setIsLoading(false);
      }
    };

    fetchUserProfile();
  }, []);

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setUserProfile(prev => ({
      ...prev,
      [name]: value
    }));
  };

  const handleLifestyleChange = (key, value) => {
    setUserProfile(prev => ({
      ...prev,
      lifestyle: {
        ...prev.lifestyle,
        [key]: value
      }
    }));
  };

  const handlePreferenceChange = (key, value) => {
    setUserProfile(prev => ({
      ...prev,
      preferences: {
        ...prev.preferences,
        [key]: value
      }
    }));
  };

  const handleSaveProfile = async () => {
    try {
      setIsLoading(true);
      setError(null);

      const payload = {
        ...userProfile,
        fullname: userProfile.name,
        profilePicture: userProfile.avatar,
        budgetMin: userProfile.budget?.min,
        budgetMax: userProfile.budget?.max,
      };

      await userService.updateProfile(payload);
      setIsEditing(false);
    } catch (err) {
      setError(err.message || 'Failed to update profile');
    } finally {
      setIsLoading(false);
    }
  };

  const handleImageUpload = (e) => {
    const file = e.target.files[0];
    if (file) {
      if (file.size > 5 * 1024 * 1024) {
        alert('Please choose an image under 5MB');
        return;
      }

      setIsUploading(true);
      const reader = new FileReader();

      reader.onload = (e) => {
        setUserProfile(prev => ({
          ...prev,
          avatar: e.target.result
        }));
        setIsUploading(false);
      };

      reader.onerror = () => {
        alert('Error reading file');
        setIsUploading(false);
      };

      reader.readAsDataURL(file);
    }
  };

  const tabSections = [
    { id: 'personal', label: 'Personal Info', icon: 'ðŸ‘¤' },
    { id: 'preferences', label: 'Lifestyle', icon: 'âš™ï¸' },
    { id: 'roommate', label: 'Roommate Prefs', icon: 'ðŸ ' },
    { id: 'security', label: 'Security', icon: 'ðŸ”’' }
  ];

  if (isLoading) {
    return (
      <div className="profile-page">
        <Sidebar />
        <div className="main-content">
          <div className="loading-container">
            <div className="loading-spinner"></div>
            <p>Loading your profile...</p>
          </div>
        </div>
      </div>
    );
  }

  if (error) {
    return (
      <div className="profile-page">
        <Sidebar />
        <div className="main-content">
          <div className="error-container">
            <div className="error-icon">âš ï¸</div>
            <h3>Something went wrong</h3>
            <p>{error}</p>
            <button onClick={() => window.location.reload()} className="retry-btn">
              Try Again
            </button>
          </div>
        </div>
      </div>
    );
  }

  return (
    <div className="profile-page">
      <Sidebar />
      <button
        className="mobile-menu-trigger"
        onClick={() => setIsMobileMenuOpen(true)}
      >
        â˜°
      </button>
      <MobileMenu isOpen={isMobileMenuOpen} onClose={() => setIsMobileMenuOpen(false)} />

      <div className="main-content">
        <div className="profile-container">
          {/* Modern Header Section */}
          <div className="profile-header">
            <div className="header-background"></div>
            <div className="header-content">
              <div className="profile-avatar-section">
                <div className="avatar-wrapper">
                  {userProfile.avatar ? (
                    <img
                      className="profile-avatar-large"
                      src={userProfile.avatar}
                      alt={userProfile.name || 'User Avatar'}
                      onError={(e) => {
                        e.target.src = '/images/default-avatar.svg';
                      }}
                    />
                  ) : (
                    <div className="profile-avatar-large default">
                      <span>ðŸ‘¤</span>
                    </div>
                  )}
                  {isEditing && (
                    <div className="avatar-edit-overlay">
                      <input
                        type="file"
                        id="avatar-upload"
                        accept="image/*"
                        onChange={handleImageUpload}
                        style={{ display: 'none' }}
                      />
                      <label htmlFor="avatar-upload" className="avatar-edit-btn">
                        <span>ï¿½</span>
                      </label>
                    </div>
                  )}
                </div>

                <div className="profile-info">
                  <div className="name-section">
                    <h1 className="profile-name">
                      {userProfile.name || 'Your Name'}
                    </h1>
                    {userProfile.verified && (
                      <span className="verified-badge">
                        <span className="check-icon">âœ“</span>
                      </span>
                    )}
                  </div>
                  <p className="profile-subtitle">
                    {userProfile.course && userProfile.year 
                      ? `${userProfile.course} â€¢ ${userProfile.year}` 
                      : 'Student'
                    }
                  </p>
                  <p className="profile-location">
                    <span className="location-icon">ðŸ“</span>
                    {userProfile.location || 'Add your location'}
                  </p>

                  <div className="profile-stats">
                    <div className="stat-item">
                      <div className="stat-number">{userProfile.trustScore || 85}%</div>
                      <div className="stat-label">Trust Score</div>
                    </div>
                    <div className="stat-item">
                      <div className="stat-number">12</div>
                      <div className="stat-label">Connections</div>
                    </div>
                    <div className="stat-item">
                      <div className="stat-number">4.8</div>
                      <div className="stat-label">Rating</div>
                    </div>
                  </div>
                </div>
              </div>

              <div className="header-actions">
                {isEditing ? (
                  <div className="edit-actions">
                    <button 
                      className="btn-secondary"
                      onClick={() => setIsEditing(false)}
                      disabled={isLoading}
                    >
                      Cancel
                    </button>
                    <button 
                      className="btn-primary"
                      onClick={handleSaveProfile}
                      disabled={isLoading}
                    >
                      {isLoading ? 'Saving...' : 'Save Changes'}
                    </button>
                  </div>
                ) : (
                  <div className="view-actions">
                    <button 
                      className="btn-outline"
                      onClick={() => navigate('/dashboard')}
                    >
                      <span>ðŸ </span>
                      Dashboard
                    </button>
                    <button 
                      className="btn-primary"
                      onClick={() => setIsEditing(true)}
                    >
                      <span>âœï¸</span>
                      Edit Profile
                    </button>
                  </div>
                )}
              </div>
            </div>
          </div>

          {/* Tab Navigation */}
          <div className="tab-navigation">
            {tabSections.map(tab => (
              <button
                key={tab.id}
                className={`tab-btn ${activeTab === tab.id ? 'active' : ''}`}
                onClick={() => setActiveTab(tab.id)}
              >
                <span className="tab-icon">{tab.icon}</span>
                <span className="tab-label">{tab.label}</span>
              </button>
            ))}
          </div>

          {/* Tab Content */}
          <div className="tab-content">
            {activeTab === 'personal' && (
              <div className="content-section">
                <div className="section-header">
                  <h2>Personal Information</h2>
                  <p>Manage your basic profile information</p>
                </div>

                <div className="form-grid">
                  <div className="form-group">
                    <label className="form-label">Full Name</label>
                    {isEditing ? (
                      <input
                        type="text"
                        name="name"
                        value={userProfile.name}
                        onChange={handleInputChange}
                        className="form-input"
                        placeholder="Enter your full name"
                      />
                    ) : (
                      <div className="form-value">{userProfile.name || 'Not provided'}</div>
